#include <cstdio>
#include <cstdlib>
#include <algorithm>
using namespace std;
const int INT_MAX = 0x7fffffff;
int main(void) {
  char given[10][10];
  int n, k;
  while (scanf("%d %d", &n, &k) == 2) {
    for (int i = 0; i < n; ++i) {
      scanf("%s\n", given[i]);
      for (int j = 0; j < k; ++j) {
        given[i][j] -= '0';
      }
    }

    int order[10];
    for (int i = 0; i < k; ++i) order[i] = i;

    int ret = INT_MAX;
    do {
      int rearrange[10];
      int low = INT_MAX, high = -1;
      for (int i = 0; i < n; ++i) {
        int conv = 0;
        for (int j = 0; j < k; ++j) {
          conv = 10 * conv + given[i][order[j]];
        }
        low = min(low, conv);
        high = max(high, conv);
      }
      ret = min(ret, high - low);
    } while (next_permutation(order, order + k));

    printf("%d\n", ret);
  }
  return 0;
}
