#include <stdio.h>
#include <string.h>
#include <algorithm>

using namespace std;

typedef long long ll;
typedef unsigned long long ull;

int c[111111];

int main(void)
{
    freopen("input.txt","rt",stdin);
    freopen("output.txt","wt",stdout);
    int n = 0;
    scanf("%d",&n);
    for(int i = 0;i < n;i++) scanf("%d",&c[i]);
    sort(c,c+n);

    int ans = n-1;
    for(int i = 0;i < n;i++)
    {
        int tans = i+(n-(upper_bound(c,c+n,2*c[i])-c));
        ans = min(ans,tans);
    }
    printf("%d\n",ans);
    while(getchar() != EOF);
    return 0;
}
