#include<bits/stdc++.h>
using namespace std;
typedef long long ll;
typedef pair<int,int> pii;
typedef vector<int> vi;
typedef vector<pii> vpii;
typedef map<int,int> mii;
#define itr iterator
char s[555][555];
int main()
{
	int n,m;
	int c=0;
	scanf("%d%d",&n,&m);
	for(int i=1;i<=n;++i)
	{
		scanf("%s",s[i]+1);
		for(int j=1;j<=m;++j)
		{
			if(s[i][j]=='*')++c;
		}
	}
	for(int i=1;i<=n;++i)
	{
		for(int j=1;j<=m;++j)
		{
			if(s[i][j]=='*')
			{
				int qwq=1;
				if(s[i-1][j]!='*'||s[i][j-1]!='*'||s[i+1][j]!='*'||s[i][j+1]!='*')qwq=123456789;
				for(int k=i-1;k;--k)
				{
					if(s[k][j]=='*')++qwq;
					else break;
				}
				for(int k=i+1;k<=n;++k)
				{
					if(s[k][j]=='*')++qwq;
					else break;
				}
				for(int k=j-1;k;--k)
				{
					if(s[i][k]=='*')++qwq;
					else break;
				}
				for(int k=j+1;k<=m;++k)
				{
					if(s[i][k]=='*')++qwq;
					else break;
				}
				if(qwq==c)
				{
					puts("YES");
					return 0;
				}
			}
		}
	}
	puts("NO");
	return 0;
}
