#include <stdio.h>
#include <string.h>

int main()
{
  //freopen("input.txt", "rt", stdin);
  //freopen("output.txt", "wt", stdout);

  int n, t, c;
  scanf("%d %d %d", &n, &t, &c);
  int prev = -1;
  int res = 0;
  for (int i = 0; i < n; i++)
  {
    int v;
    scanf("%d", &v);
    if (v <= t && i - prev >= c)
      res++;
    else if (v > t)
      prev = i;
  }

  printf("%d", res);

  return 0;
}
