#include<bits/stdc++.h>
#define ll long long
#define il inline
using namespace std;
const int maxn=505;
il int read(){
	int x=0;
	char c=getchar();
	for(;!(c>='0'&&c<='9');c=getchar());
	for(;c>='0'&&c<='9';c=getchar())
		x=(x<<1)+(x<<3)+c-'0';
	return x;
}
int T,n,m;
char a[maxn],b[maxn];
bool chka(){
	for(int i=1;i<n;i++)
		if(a[i]==a[i+1]) return 0;
	return 1;
}
bool chkb(){
	for(int i=1;i<m;i++)
		if(b[i]==b[i+1]) return 0;
	return 1;
}
int main(){
	T=read();
	while(T--){
		n=read(),m=read();
		scanf("%s%s",a+1,b+1);
		if(chka()){
			printf("Yes\n");
			continue;
		}
		if(!chkb()){
			printf("No\n");
			continue;
		}
		if(b[1]!=b[m]){
			printf("No\n");
			continue;
		}
		bool f0=0,f1=0;
		for(int i=1;i<n;i++)
			if(a[i]=='0'&&a[i+1]=='0') f0=1;
			else if(a[i]=='1'&&a[i+1]=='1') f1=1;
		if(f0&&f1){
			printf("No\n");
			continue; 
		}
		if(b[1]=='1'&&f1){
			printf("No\n");
			continue;
		}
		if(b[1]=='0'&&f0){
			printf("No\n");
			continue;
		}printf("Yes\n");
	}
	return 0;
}
