#include<bits/stdc++.h>
using namespace std;
using LL = long long;
int main() {
    cin.tie(nullptr)->sync_with_stdio(false);
    cout << fixed << setprecision(20);
    int t;
    for (cin >> t; t; t -= 1) {
        int n;
        cin >> n;
        vector<LL> a(n);
        for (LL& x : a) cin >> x;
        ranges::sort(a);
        LL s = 0;
        for (int i = 0; i < (n - 1) / 2; i += 1)
            s += a[n - i - 1];
        for (int i = 0; i < (n - 1) / 2 + 1; i += 1)
            s -= a[i];
        cout << (s > 0 ? "YES\n" : "NO\n");
    }
    return 0;
}
