#include<algorithm>
#include<cstdio>
#include<vector>
const int maxn=2e5+5;
int t;
int n,k;
int a[maxn];
int gup(int x,int v){return (x+v-1)/v*v;}
signed main(){
	scanf("%d",&t);
	for(int wc=1;wc<=t;wc++){
		scanf("%d%d",&n,&k);
		for(int i=1;i<=n;i++)scanf("%d",a+i);
		int ans=k,mi=k,imi=k;
		for(int i=1;i<=n;i++)ans=std::min(ans,gup(a[i],k)-a[i]);
		if(k==4){
			for(int i=1;i<=n;i++){
				int nv=gup(a[i],2)-a[i];
				if(nv<mi)imi=mi,mi=nv;
				else if(nv<imi)imi=nv;
			}
			ans=std::min(ans,imi+mi);
		}
		printf("%d\n",ans);
	}
	return 0;
}
