using namespace std;

int n, k;
int a[10000], b[10000];

int main() {
	freopen("input.txt", "r", stdin);
	freopen("output.txt", "w", stdout);
	scanf("%d%d", &n, &k);
	for(int i=0; i<n; i++) scanf("%d", &a[i]);
	for(int i=0; i<n; i++) b[i] = a[i];
	sort(b, b+n); reverse(b, b+n);
	int v = b[k-1];
	printf("%d\n", v);
	for(int i=0; i<n; i++) {
		if(a[i] >= v) {
			if(k > 0) {
				printf("%d", i+1);
				k--;
				if(k == 0) printf("\n"); else printf(" ");
			}
		}
	}
	return 0;
}
