#include <iostream>
#include <cstdio>
using namespace std;
int i,j,n,k;
int a[100010];
void Qsort(int l,int r)
{
	int i=l,j=r,x=a[(l+r)>>1],t;
	while(i<=j)
	{
		while(a[i]<x) i++;
		while(x<a[j]) j--;
		if(i<=j)
		{
			t=a[i]; a[i]=a[j]; a[j]=t;
			i++,j--;
		}
	}
	if(l<j) Qsort(l,j);
	if(i<r) Qsort(i,r);
}
int main()
{
	scanf("%d%d",&n,&k);
	for(i=1;i<=n;i++) scanf("%d",&a[i]);
	Qsort(1,n);
	for(i=1;i<=n;i++)
	{
		if(k==0) break;
		if(a[i]<0)
		{
			a[i]=-a[i];
			k--;
		}
	}
	if(k%2==1)
	{
		Qsort(1,n);
		a[1]=-a[1];
	}
	j=0;
	for(i=1;i<=n;i++) j+=a[i];
	cout << j << endl;
	//system("pause");
	return 0;
}
