#include<bits/stdc++.h>
using namespace std;

int T;

int n,m;

int main()
{
	ios::sync_with_stdio(false);
	cin.tie(0);
	cin>>T;
	while(T--)
	{
		int n,x,y;
		cin>>n>>x>>y;
		if(x==0&&y==0)
		{
			cout<<"-1\n";
			continue;
		}
		if(x!=0&&y!=0)
		{
			cout<<"-1\n";
			continue;
		}
		if(!x)
			swap(x,y);
		if((n-1)%x==0)
		{
			int c=x;
			int w=1;
			for(int i=2;i<=n;i++)
			{
				cout<<w<<(" \n"[i==n]);
				c--;
				if(!c)
					w=i+1,c=x;
			}
		}
		else
			cout<<"-1\n";
	}
}
