#include <string>
#include <vector>

using namespace std;

char A[200000];
char B[200000];
int n;
int C[200000];
int D[200000];

int main() {
  scanf ("%d", &n);
  for (int i = 0; i < n; ++i) scanf ("%d %d", C + i, D + i);

  A[n] = 0;
  B[n] = 0;
  for (int i = 0; i < n/2; ++i) A[i] = B[i] = '1';
  for (int i = n/2; i < n; ++i) A[i] = B[i] = '0';

  int a = 0, b = 0;
  for (int i = 0; i < n; ++i) {
    if (C[a] < D[b]) {A[a] = '1'; ++a;}
    else {B[b] = '1'; ++b;}
  }

  printf("%s\n%s\n", A, B);

  return 0;
}
