using namespace::std;

int n,m;
int a[1005][1005];

int main(){
    scanf("%d %d",&n,&m);
    for(int i=1;i<=n;i++){
        for(int j=1;j<=m;j++)
            scanf("%d",&a[i][j]);
    }
    while(n%2==0){
        int n1=n/2;
        bool chk=true;
        for(int i=1;i<=n1;i++){
            int i1=n-i+1;
            for(int j=1;j<=m;j++) chk&=a[i][j]==a[i1][j];
        }
        if(!chk) break;
        n=n1;
    }
    printf("%d",n);
    return 0;
}
