#include<bits/stdc++.h>
using namespace std;
inline int read()
{
	int sum=0,f=1;
	char ch=getchar();
	while(!isdigit(ch)){if(ch=='-')f*=-1;ch=getchar();}
	while(isdigit(ch)){sum=sum*10+ch-48;ch=getchar();}
	return sum*f;
}
int b[100005];
int main()
{
	int T;
	T=read();
	while(T--)
	{
		int n;
		n=read();
		int left=0,used=0,ans=0;
		for(int i=1;i<=n;i++)
		{
			int x;
			x=read();
			if(x==1)left++;
			if(x==2)
			{
				if(!left)continue;
				if(left&1) used+=left/2,left=1;
				else used+=(left-2)/2,left=2;
			}
			ans=max(ans,used+left);
		}
		cout<<ans<<endl;
	}
	return 0;
}
