#include <cstdio>
#include <cstring>
#include <cstdlib>
#include <cmath>
#include <map>
#include <set>
#include <vector>
#include <algorithm>
#include <iostream>

using namespace std;

int a[105];

int main (void)
{
  int n, k;
  scanf("%d%d", &n, &k);

  for (int i=0; i<n; i++)
    scanf("%d", &a[i]);

  int turn = 0;
  while (a[0]<k) {
    for (int j=0; j<n; j++) {
      if ((j == n-1 || a[j] != a[j+1]) && a[j] < k) {
        a[j]++;
      }
    }
    turn++;
  }

  printf ("%d\n", turn);


  return 0;
}
