#include <bits/stdc++.h>

using namespace std;

int main() {
    ios_base::sync_with_stdio(false); cin.tie(NULL);

    int ntest; cin >> ntest;
    while(ntest--) {
        int n; cin >> n;
        vector<int> a(n);
        for(int i = 0; i < n; ++i) cin >> a[i];

        sort(a.begin(), a.end());

        int ans = 0, cntzero = 0, cntlarger = 0;
        for(int i = 0; i < n; ++i) {
            if(a[i] < 0) ans++;
            else {
                if (a[i] == 0) cntzero += 1;
            }
        }

        int res = ans + cntzero, mn = 1e9;
        for(int i = 0; i + 1 < n; ++i) {
            if (a[i + 1] > 0) break;
            mn = min(mn, a[i + 1] - a[i]);
        }

        for(int i = 0; i < n; ++i) if (a[i] > 0 && a[i] <= mn) cntlarger += 1;

        res = max(res, ans + (cntzero > 0) + (cntlarger > 0));

        cout << res << "\n";
    }

    return 0;
}
