#define PI 3.1415926535897932384626433832795
#define EPS 1e-9

#define sqr(x) ((x) * (x))
#define ABS(a) ((a)<0?-(a):(a))
#define EQ(a,b) (ABS((a)-(b))<EPS)

#define all(a) a.begin(), a.end()
#define two(i) (1 << (i))
#define has(mask, i) ((((mask) & two(i)) == 0) ? false : true)

const int inf = 1000 * 1000 * 1000;
const LL inf64 = 1000LL * 1000LL * 1000LL * 1000LL * 1000LL * 1000LL;

int a[2000];
int n;

void Load()
{
	scanf ("%d", &n);
	for (int i = 0; i < n; i++)
		scanf ("%d", &a[i]);
}

void Solve()
{
	if (n == 1)
	{
		cout << "0 0";
		return;
	}
	int l = 0, r = 0;
	for (int i = 0; i < n - 1; i++)
		if (a[i] > a[i + 1])
		{
			l = i;
			r = i;
			while (r < n - 1 && a[r] > a[r + 1])	
				r++;
			break;
		}
   	bool fl = true;
   	for (int i = 0; i < l - 1;i++)
    	if (a[i] != a[i + 1] - 1)
    		fl = false;

    for (int i = l; i < r; i++)
   		if (a[i] != a[i + 1] + 1) 	
   			fl = false;
   	for (int i = r + 1; i < n - 1; i++)
   		if (a[i] != a[i + 1] - 1)
   			fl = false;

   	if (fl && r > l)
   	{
   		cout << l + 1 << " " << r + 1;
   	}
   	else
   	{
   		cout << "0 0";
   	}

}

int main()
{
	//ios_base::sync_with_stdio(0);
#ifndef ONLINE_JUDGE
