#include <iostream>
#include <cstdio>
#include <vector>
#include <cstring>
#include <algorithm>
#include <set>
#include <unordered_map>
using namespace std;

int n;
int a[2001][2001];

int main()
{
    ios_base::sync_with_stdio(false);
    int n, m;
    cin >> n >> m;
    for (int i = 1; i <= n; i++)
    {
        for (int g = 1; g <= m; g++)
        {
            cin >> a[i][g];
        }
    }

    int ans = 0;
    for (int i = 1; i <= n; i++)
    {
        bool t = false;
        for (int g = 1; g <= m; g++)
        {
            if (!a[i][g])
                ans += t;
            if (a[i][g])
            {
                t = true;
            }
        }

        t = false;
        for (int g = m; g >= 1; g--)
        {
            if (!a[i][g])
                ans += t;
            if (a[i][g])
            {
                t = true;
            }
        }
    }

    for (int g = 1; g <= m; g++)
    {
        bool t = false;
        for (int i = 1; i <= n; i++)
        {
            if (!a[i][g])
            {
                ans += t;
            }

            if (a[i][g])
            {
                t = true;
            }
        }

        t = false;
        for (int i = n; i >= 1; i--)
        {
            if (!a[i][g])
            {
                ans += t;
            }

            if (a[i][g])
            {
                t = true;
            }
        }
    }

    cout << ans << endl;
    return 0;
}
