#include <cstdio>
#include <algorithm>

int aa[100], n, k;

int main() {
    scanf("%d%d", &n, &k);
    for(int i=0; i<n; i++) scanf("%d", aa+i);
    std::sort(aa, aa+n);
    aa[n] = aa[n-1]+1;
    int i;
    for(i=0; i<n; i++) {
        if(aa[i] == aa[i+1]) continue;
        if(i == n-k) {
            printf("%d %d\n", aa[i], aa[i]);
            break;
        }
    }
    if(i == n) puts("-1");
    return 0;
}
