#include <bits/stdc++.h>
using namespace std;

int main() {
    ios::sync_with_stdio(false), cin.tie(0);
    int t;
    cin >> t;
    while (t--) {
        int n;
        cin >> n;
        vector <int> forbid(n, 0);
        int full = (1 << 30) - 1;
        vector <vector <int>> a(n, vector <int>(n));
        for (int i = 0; i < n; ++i) {
            for (int j = 0; j < n; ++j) {
                cin >> a[i][j];
                if (i != j) {
                    forbid[i] |= full ^ a[i][j];
                    forbid[j] |= full ^ a[i][j];
                }
            }
        }
        for (int i = 0; i < n; ++i) {
            forbid[i] ^= full;
        }
        bool ok = true;
        for (int i = 0; i < n; ++i) for (int j = 0; j < n; ++j) {
            ok &= i == j || (forbid[i] | forbid[j]) == a[i][j];
        }
        if (ok) {
            cout << "YES\n";
            for (int i = 0; i < n; ++i) {
                cout << forbid[i] << " \n"[i + 1 == n];
            }
        } else {
            cout << "NO\n";
        }
    }
}
