string s[110];
const int mod=1000000007;

int n, m;

LL get(int cx, int cy, int dx, int dy){
	LL left=0, right=1LL<<35;
	while( left+1<right ){
		LL mid=(left+right)/2;
		LL tx=cx+dx*mid;
		LL ty=cy+dy*mid;
		if( tx<1 || tx>n || ty<1 || ty>m ) right=mid;
		else left=mid;
	}
	return left;
}
int main(){

	while( scanf("%d %d", &n, &m)!=EOF ){
		int cx, cy;
		scanf("%d %d", &cx, &cy);
		LL ans=0;
		int k; scanf("%d",&k);
		while( k-- ){
			int dx, dy; scanf("%d %d", &dx, &dy);
			LL step=get(cx, cy, dx, dy);
			ans+=step;
			cx+=dx*step;
			cy+=dy*step;
		}
		cout<<ans<<endl;
	}
}
