using namespace std;
typedef long long LL;
typedef pair< int, int > PRII;
typedef pair< double, double > PRDD;
typedef vector< string > VS;
typedef vector< int > VI;

#define x first
#define y second
#define p_b push_back
#define m_p make_pair
#define oo 1000000000

#define sqr(x) ((x)*(x))
#define Size(a) ((int)a.size())
#define forn(i,n) for( i = 0 ; i < n ; ++i )

const double pi = acos(-1);

#define maxn 100 + 10

int a[maxn],c[maxn][maxn];
bool hash[maxn] = {0};

int main()
{
    int n,m,ans = 0;
    int i,j,k,t,u,v;

    scanf("%d%d",&n,&m);
    for( i = 1 ; i <= m ; ++i )
        {
            scanf("%d%d",&u,&v);
            c[u][v] = c[v][u] = 1;
        }

    while(1)
        {
            k = 0;
            for( i = 1 ; i <= n ; ++i )
                if( !hash[i] )
                    {
                        t = 0;
                        for( j = 1 ; j <= n ; ++j )
                            if( !hash[j] && c[i][j] )
                                ++t;
                        if( t == 1 )
                            a[++k] = i;
                    }
            for( i = 1 ; i <= k ; ++i )
                hash[a[i]] = 1;
            if( !k )
                break;
            ++ans;
        }

    printf("%d\n",ans);

    return 0;
}
