#include <iostream>
#include <string>
#include <set>
#include <map>
#include <vector>


using namespace std;

string s;

int getSum(int x){
    int ans = 0;
    while(x){
        ans += x%10;
        x/=10;
    }
    return ans;
}

int main(){
    cin >> s;
    if(s.length()==1){
        cout << 0;
        return 0;
    }
    int sum = 0;
    for(int i=0; i<s.length(); i++){
        sum += (s[i]-'0');
    }
    int ans = 1;
    while(sum>9){
        sum = getSum(sum);
        ans++;
    }
    cout << ans;

    return 0;
}
