#include <bits/stdc++.h>
using namespace std;
#define int long long
#define IOS ios::sync_with_stdio(0) , cin.tie(0) , cout.tie(0)
inline int in(){int32_t x ; scanf("%d",&x) ; return x ;}
inline long long lin(){long long x ; scanf("%lld",&x) ; return x;}
const int maxn = 1e6 + 200;
const int mod = 1e9 + 7;
const long long infll = 2e18 + 10;
const int inf = 1e9 + 10;
typedef pair<int,int> pii;
typedef long long ll;
typedef vector<int> vi;
int k[maxn],l[maxn],extra[maxn],id[maxn];
inline int Cnt(int a , int b) {return min(a,b);}
int32_t main()
{
    int n = in() , f = in();
    int res = 0;
    for (int i = 0 ; i < n ; i ++){

        k[i] = in() , l[i] = in() , extra[i] = Cnt(2LL *k[i],l[i])-Cnt(k[i],l[i]);
        res += Cnt(k[i],l[i]);
    }
    iota(id,id+n,0);
    sort(id,id+n,[](const int &a , const int &b) {return extra[a] > extra[b];});
    for (int i = 0 ; i < f ; i ++) res += extra[id[i]];
    cout << res << endl;
}
