#include <bits/stdc++.h>

using namespace std;

bool tshirt(int p, int score) {
    int r = (score/50)%475;
    for(int i=0;i<25;i++) {
        r = (r*96+42)%475;
        if(r+26 == p) {
            return true;
        }
    }
    return false;
}

int main() {
    int p, x, y;
    cin >> p >> x >> y;
    int score = x;
    while(score-50 >= y) {
        score -= 50;
    }
    while(!tshirt(p,score)) {
        score += 50;
    }
    score -= x;
    score += 50;
    if(score < 0) {
        score = 0;
    }
    cout << score/100 << endl;
}
