using namespace std;

int main() {
  int n;
  scanf("%d", &n);
  int k = 0;
  int l = 0;
  while (n) {
    const int t = n % 10;
    if (t == 7) {
      k |= (1 << l);
    }
    n /= 10;
    l++;
  }

  k += (1 << l) - 1;

  printf("%d\n", k);

  return 0;
}
