#include<bits/stdc++.h>
using namespace std;

typedef long long ll;
typedef double db;

inline ll read() {
	ll x=0,f=1;
	char ch=getchar();
	while(ch<'0'||ch>'9') {
		if(ch=='-') f=-f;
		ch=getchar();
	}
	while(ch>='0'&&ch<='9') {
		x=(x<<3)+(x<<1)+ch-'0';
		ch=getchar();
	}
	return x*f;
}
inline void write(ll x) {
	if(x<0) putchar('-'),x=-x;
	if(x>=10) write(x/10);
	putchar(x%10+'0');
}
#define writesp(x) write(x),putchar(' ')
#define writeln(x) write(x),putchar('\n')

#define rep(x,l,r) for(int x=l;x<=r;x++)
#define per(x,r,l) for(int x=r;x>=l;x--)
#define pb push_back
#define mp make_pair
#define all(v) v.begin(),v.end()

const int N=200200;

int n;
int a[N];

void solve() {
	n=read(); rep(i,1,n) a[i]=read();
	int mn=a[1];
	rep(i,1,n) mn=min(mn,a[i]);
	ll ans=0;
	rep(i,1,n) {
		int x=(a[i]-1)/(mn*2-1);
		ans+=x;
	}
	writeln(ans);
}

int main() {
	int t=read(); while(t--) solve();
	return 0;
}
