#include <stdio.h>
#include <string.h>

int main(){

    int test;
    int ans=0,now=0;
    long long prev1,prev2,temp;

    for( scanf("%d",&test) ; test-- ; ){
        if( now==0 ){
            scanf("%I64d",&prev2);
            now=1;
        }
        else if( now==1 ){
            scanf("%I64d",&prev1);
            now=2;
        }
        else{
            scanf("%I64d",&temp);
            if( prev1+prev2!=temp ){
                now=2;  
            }
            else{
                now++;
            }
            prev2=prev1;
            prev1=temp; 
        }

        if( now>ans ) ans=now;
    }
    printf("%d\n",ans);
    return 0;
}
