typedef long long ll; 
typedef pair<ll, ll> pll;
const double PI = 3.14159265358979323846;  



int main() {
	int n;
	cin >> n;
	vector<int> rad(n);
	for (int i = 0; i < n; ++i)
		cin >> rad[i];
	sort(rad.begin(), rad.end());
	int k = 1;
	int sum = 0;
	for (int i = rad.size() - 1; i >= 0; --i) {
		sum = sum + k * rad[i] * rad[i];
		k = -k;
	}
	double s = PI * sum;
	printf("%0.10lf\n", s);
	return 0;	
}
