#include <iostream>
#include <cstdio>
#include <cmath>
#include <cstring>
#include <vector>
#include <algorithm>
#include <cstdlib>
#include <string>
#include <queue>
#include <map>
#include <set>

#define MAX 1024

using namespace std;
FILE *in; FILE *out;

int l, d, v, g, r;
#define EPS 0.000000001

int main(void)
{
	in = stdin; out = stdout;
//	in = fopen("B.in", "rt"); out = fopen("B.out", "wt");

	fscanf(in, "%d %d %d %d %d", &l, &d, &v, &g, &r);
	double speed = 1.0 / (double)v, ans = 0.0;
	for (int i = 0; i < d; i++)
		ans += speed;
	double rem = ans;
	while (rem >= (g + r)) rem -= g + r;
	if (rem + EPS > g) ans += r + g - rem;
	for (int i = d; i < l; i++) ans += speed;
	fprintf(out, "%.8lf\n", ans);

	return 0;
}
