#include<bits/stdc++.h>
using namespace std;
typedef long long int uli;
int main(){
  int n;
  scanf("%d",&n);
  int h=2e9;
  for(int i=0;i<n;i++){
    int wi,hi;
    scanf("%d %d",&wi,&hi);
    if(hi<wi)swap(wi,hi);
    if(hi>h && wi>h){
      puts("NO");
      return 0;
    }
    if(hi<=h)h=hi;
    else h=wi;
  }
  puts("YES");
  return 0;
}
