#include <stdio.h>
#include <string.h>
#include <iostream>
#include <algorithm>
#include <vector>
#include <queue>
#include <set>
#include <map>
#include <string>
#include <math.h>
#include <stdlib.h>
#include <time.h>
using namespace std;

int a[3030];
pair<int,int>p[3030];

int main()
{
    //freopen("in.txt","r",stdin);
    //freopen("out.txt","w",stdout);
    int n,v;
	while(scanf("%d%d",&n,&v) == 2)
	{
		memset(a,0,sizeof(a));
		for(int i = 0;i < n;i++)
			scanf("%d%d",&p[i].first,&p[i].second);
		sort(p,p+n);
		int ans = 0;
		int a1,b1;
		for(int i = 0;i < n;i++)
		{
			a1 = p[i].first;
			b1 = p[i].second;
			if(b1 > 0)
			{
				int add = min(v-a[a1],b1);
				ans += add;
				a[a1] += add;
				b1 -= add;
			}
			if(b1 > 0)
			{
				int add = min(v-a[a1+1],b1);
				ans += add;
				a[a1+1] += add;
				b1 -= add;
			}
		}
		printf("%d\n",ans);
	}
    return 0;
}
