#include <bits/stdc++.h>
using namespace std;

int main(){
	int l = 0, r, n;
	cin >> n >> r;
	vector<int> vec;
	for(int i = 0 ; i < n ; i++){
		int x;
		cin >> x;
		vec.push_back(x);
	}
	sort(vec.begin(), vec.end());

	int ans = max(vec[0], r-vec.back()) * 2;
	for(int i = 1 ; i < vec.size() ; i++){
		ans = max(vec[i] - vec[i-1], ans);
	}
	if(ans&1){
		cout << ans / 2 << ".5000000000" << endl;
	}
	else{
		cout << ans / 2 << ".0000000000" << endl;
	}
}
