#include <iostream>
#include <cstdio>
#include <string.h>
#include <string>
#include <algorithm>
#include <cmath>
#include <vector>

using namespace std;
long long a[1111],b[1111],cost[11],n,m;

int main()
{
	for(int i=0; i<4; i++)
	{
		cin >> cost[i];
	}
	cin >> n >> m;
	for(int i=0; i<n; i++) cin >> a[i]; 
	for(int i=0; i<m; i++) cin >> b[i];
	long long ans=min(cost[3],2*cost[2]);
	long long mi=0;
	for(int i=0; i<n; i++) 
	{
		long long cur =min(a[i]*cost[0],cost[1]);
		mi+=cur;
	} 
	mi=min(mi,cost[2]);
	long long mmi=0;
	for(int i=0; i<m; i++) 
	{
		long long cur=min(b[i]*cost[0],cost[1]);
		mmi+=cur;
	} 	
	mmi=min(mmi,cost[2]);
	mi+=mmi;
	ans=min(mi,ans);
	cout << ans << endl;	
	cin >> n;
	return 0;
}
