#include<iostream>
#include<string.h>
#include<vector>
#include<algorithm>
#include<iomanip>
#include<map>
using namespace std;

long long a[1000006], b[1000006];
long long acnt, bcnt;
long long atotal, btotal;

int main()
{
    int n;
    cin>>n;
    acnt = bcnt = 0;
    atotal = btotal = 0;
    string ans;

    for(int i = 0; i < n; ++i)
    {
        int tmp;
        cin>>tmp;
        if(tmp > 0)
        {
            a[acnt++] = tmp;
            atotal += tmp;
            ans = "first";
        }
        else
        {
            tmp = -tmp;
            b[bcnt++] = tmp;
            btotal += tmp;
            ans = "second";
        }
    }

    if(atotal > btotal)
    {
        cout<<"first"<<endl;
        return 0;
    }

    if(atotal < btotal)
    {
        cout<<"second"<<endl;
        return 0;
    }

    for(int i = 0; i < min(acnt, bcnt); ++i)
    {
        if(a[i] > b[i])
        {
            cout<<"first"<<endl;
            return 0;
        }
        if(a[i] < b[i])
        {
            cout<<"second"<<endl;
            return 0;
        }
    }

    if(acnt > bcnt)
    {
        cout<<"first"<<endl;
        return 0;
    }
    if(acnt < bcnt)
    {
        cout<<"second"<<endl;
        return 0;
    }

    cout<<ans<<endl;
    return 0;
}
