#include <bits/stdc++.h>
using namespace std;
#define ll long long
#define pb push_back
#define pii pair<int,int>
#define all(x) x.begin(), x.end()
#define rep(i, a, b) for(int i = (a); i < ((int)(b)); ++i)
#define per(i, a, b) for(int i = (a) - 1; i >= ((int)(b)); --i)
#define sz(a) ((int)a.size())
const int mod = 998244353, N = 200005;
int n, k, a[N];
vector<int> vec[N];

void frank() {
    cin >> n >> k;
    for (int i = 0; i < k; ++i) vec[i].clear(), vec[i].pb(-1);
    for (int i = 0; i < n; ++i) cin >> a[i], a[i]--, vec[a[i]].pb(i);
    for (int i = 0; i < k; ++i) vec[i].pb(n);
    int res = (n + 2) / 2;
    for (int i = 0; i < k; ++i) if(sz(vec[i]) > 2) {
        vector<int> vec1;
        for (int j = 0; j < sz(vec[i]) - 1; ++j) vec1.pb(vec[i][j + 1] - vec[i][j]);
        sort(all(vec1), greater<int>());
        res = min(res, max(vec1[1], (vec1[0] + 1) / 2));
    }
    cout << res - 1 << "\n";
}

int main () {
    ios::sync_with_stdio(false), cin.tie(0);
    int t;
    cin >> t;
    while (t--) {
        frank();
    }
}
