#include <iostream>
#include <vector>
#include <algorithm>
using namespace std;

#define DEB 1
#define dbg_tab(x,l) if(DEB) { cerr << #x << ":"; for(int i=0;i<l;i++) cerr << " " << x[i]; cerr << endl; }
#define dbg_vec(x) if(DEB) { cerr << #x << ":"; for(auto y:x) cerr << " " << y; cerr << endl; }

int ti[200007];

int main() {
    ios_base::sync_with_stdio(0);
    int n;
    cin >> n;
    for(int i = 1; i <= n; ++i) {
        int x;
        cin >> x;
        ti[x] = i;
    }

    int res = 1e9;
    int gd = 0;
    for(int i = 0; i <= 200000; ++i) {
        if(ti[i] && ti[i] < res) {
            res = ti[i];
            gd = i;
        }
    }

    cout << gd << endl;
    return 0;
}
