#include <iostream>
#include <cstring>
#include <cstdlib>
#include <cstdio>

using namespace std;

int a[5000000];
int sum[5000000];
int ans[5000000],tot;
int s,k,n;

int main()
{
	scanf("%d%d",&s,&k);
	a[1]=1;
	sum[1]=1;
	for(int i=2;;i++)
	{
		if(i<=k)
			a[i]=sum[i-1];
		else a[i]=sum[i-1]-sum[i-k-1];
		if(a[i]>s) {n=i;break;}
		sum[i]=sum[i-1]+a[i];
	}
	for(int i=n-1;i>=1;i--){
		if(s-a[i]>=0)
		{
		 s-=a[i];
		 ans[++tot]=a[i];
		}
	}
	if(tot==1) ans[++tot]=0;
	printf("%d\n",tot);
	for(int i=1;i<=tot;i++) printf("%d ",ans[i]);
	return 0;
}
