#include<cstdio>
#include<cstdlib>
#include<cstring>
#include<algorithm>
#include<cmath>
#define MAXN 100005
#define INF 0x7fffffffffffffffLL
using namespace std ;
typedef long long LL ;

LL A, B, N, sq ;
LL ta, tb, ba, bb, ans = INF ;

int main()
{
	int i, j ;
	scanf("%I64d %I64d %I64d", &N, &A, &B) ;
	N *= 6,	sq = sqrt(N)+3 ;
	for(ta = A; ta <= max(A, sq); ta ++)
	{
		tb = max(B, N/ta+(N%ta!=0)) ;
		if(ta*tb < ans) ans = ta*tb, ba = ta, bb = tb ;
	}
	for(tb = B; tb <= max(B, sq); tb ++)
	{
		ta = max(A, N/tb+(N%tb!=0)) ;
		if(ta*tb < ans) ans = ta*tb, ba = ta, bb = tb ;
	}
	printf("%I64d\n%I64d %I64d\n", ans, ba, bb) ;
	//system("pause") ;
	return 0 ;
}
