#include <bits/stdc++.h>
#define int long long
#define x first
#define y second
#define pb push_back
using namespace std;
using pii=pair <int,int>;
using tii=pair <pii,int>;
void solve(){
    int n;
    cin>>n;
    string s;
    cin>>s;
    int diff=0,same=0;
    int l=0,r=n-1;
    while (l<r){
        if (s[l]==s[r]) same++;
        else diff++;
        l++; r--;
    }
    bool hv=0;
    if (l==r) hv=1;
    for (int i=0; i<=n; i++){
        if (i<diff) cout<<"0";
        else if ((i-diff)%2==0){
            if ((i-diff)<=2*same) cout<<"1";
            else cout<<"0";
        } else if (hv){
            if ((i-diff-1)<=2*same) cout<<"1";
            else cout<<"0";
        } else cout<<"0";
    }
    cout<<'\n';
}
signed main(){
	ios_base::sync_with_stdio(0); cin.tie(0);
	int t=1;
	cin>>t;
	while (t--) solve();
}
