using namespace std;
typedef long long LL;
const int N = 500005;
const int inf = 1000000009;
int n , m , s , f;
int t[N] , l[N] , r[N];
int main () {
    #ifndef ONLINE_JUDGE
        freopen ("input.txt" , "r" , stdin);
        // freopen ("output.txt" , "w" , stdout);
    #endif
    cin >> n >> m >> s >> f;
    for (int i = 0 ; i < m ; i ++) {
        cin >> t[i] >> l[i] >> r[i];
    }
    int sgn = (f - s) > 0 ? 1 : -1;
    for (int i = 1 , j = 0 ; ; i ++) {
        if (s == f) break;
        int nxt = s + sgn;
        if (j < m && t[j] == i) {
            if ((s >= l[j] && s <= r[j]) || (nxt >= l[j] && nxt <= r[j]))
                putchar ('X');
            else {
                s = nxt;
                if (sgn > 0) putchar ('R');
                else putchar ('L');
            }
            j ++;
        }
        else {
            s = nxt;
            if (sgn > 0) putchar ('R');
            else putchar ('L');
        }
    }
    puts ("");
    return 0;
}
