#include<algorithm>
#include<iostream>
#include<iomanip>
#include<cstring>
#include<vector>
#include<string>
#include<cmath>
using namespace std;

int n , L[1001] , R[1001] , now;

bool check(int t)
{
    for(int i = 1 ; i <= n ; i++)
        if(L[i] > t || R[i] < t)
            return false;
    return true;
}

int main()
{
    ios::sync_with_stdio(false);
    while(cin>>n>>now)
    {
        for(int i = 1 ; i <= n ; i++)
        {
            cin>>L[i]>>R[i];
            if(L[i] > R[i])
                swap(L[i] , R[i]);
        }
        int d = 10000;
        for(int i = 0 ; i <= 1000 ; i++)
            if(check(i))
            {
                d = min(d , abs(now - i));
            }
        if(d == 10000)
            cout<<-1<<endl;
        else
            cout<<d<<endl;
    }
    return 0;
}
