#include<bits/stdc++.h>
using namespace std;
#define LL long long
#define pa pair<int,int>
const int N=200010;
LL read()
{
	LL x=0,f=1;char ch=getchar();
	while(ch<'0'||ch>'9'){if(ch=='-')f=-1;ch=getchar();}
	while(ch>='0'&&ch<='9')x=x*10ll+ch-'0',ch=getchar();
	return x*f;
}
int n,a[N];
char s[N];
void solve()
{
	n=read();
	scanf("%s",s+1);
	int ans=0,c=0;
	char lst=s[1];
	for(int i=1;i<=n;i++)
	{
		if(s[i]==lst)c++,lst=s[i];
		else
		{
			if(!(c&1))c=1,lst=s[i];
			else
			{
//				printf("i = %d\n",i);
				c++;
				ans++;
				lst='1'+'0'-s[i];
			}
		}
	}
	printf("%d\n",ans);
}
int main()
{
	int T=read();
	while(T--)solve();
 	return 0;
}
