#include <bits/stdc++.h>

using namespace std;

const int N = 2e5 + 5;

int n;
char s[N];
int cnt, mn;

void solve()
{
	scanf("%d", &n);
	scanf("%s", s + 1);
	cnt = mn = 0;
	for (int i = 1; i <= n; i += 2)
		if (s[i] ^ s[i + 1])
			cnt++;
	int pre = -1;
	for (int i = 1; i <= n; i += 2)
	{
		if (s[i] ^ s[i + 1])
			continue;
		if (s[i] == pre)
			continue;
		pre = s[i];
		mn++;
	}
	mn = max(mn, 1);
	printf("%d %d\n", cnt, mn);
}

int main()
{
	int T;
	scanf("%d", &T);
	while (T--)
		solve();
	return 0;
}
