#include <iostream>

using namespace std;

int main(void){
    long long k, b, n, t;
    cin >> k >> b >> n >> t;
    long long curr = 1, time = 0;
    while (curr < t) {
        curr = k * curr + b;
        ++time;
    }
    if (curr > t) --time;
    if (time > n) time = n;
    cout << n - time<< endl;
    return 0;
}
