#include <cstdio>
using namespace std;
int t[1000*128];
int n;

int main() {
    scanf("%d",&n);
    for(int i=0;i<n;i++)
        scanf("%d",&t[i]);
    int i=-1,j=n,ti=0,tj=0,ci=0,cj=0;
    while(i<j-1) {
        if (ti<=tj) {
            i++;
            ti+=t[i];
            ci++;
        } else {
            j--;
            tj+=t[j];
            cj++;
        }
    }
    printf("%d %d\n",ci,cj);
    return 0;
}
