#include<bits/stdc++.h>
using namespace std;
int n,a[200011];
int main(){
	scanf("%d",&n);
	if (n%2==0){puts("NO"); return 0;}
	puts("YES");
	for (int i=1;i<=n;++i) a[i]=2*i-1;
	for (int i=1;i<=n;++i) a[n+i]=2*i;
	for (int i=2;i<=n;i+=2) swap(a[i],a[n+i]);
	for (int i=1;i<=2*n;++i) printf("%d%c",a[i],i==2*n?'\n':' ');
}
