#include <bits/stdc++.h>
using namespace std; 

const int N=350000;
const int Mod=1e9+7;

long long a[N],f[N],ans;
int n,m,l[N],r[N];

int main() {
	cin>>n>>m;
	//for(int i=1;i<=n;i++) cin>>a[i];
	int ans=1e9;
	for(int i=1;i<=m;i++) {
		scanf("%d%d",l+i,r+i);
		ans=min(ans,r[i]-l[i]+1);
	}
	cout<<ans<<endl;
