#include <bits/stdc++.h>
using namespace std;

void solve_tc(){
	int n, k; 
	cin >> n >> k;

	if (k == n - 1){
		if (n == 4){
			cout << -1 << "\n";
		}
		else{
			cout << 1 << " " << n / 2 - 1 << "\n";
			cout << n / 2 << " " << 0 << "\n";
			cout << n - 1 << " " << n - 2 << "\n";
			for (int i = 0; i < n / 2; i++){
				if ((i == 0) || (i == 1) || (i == n / 2 - 1)) continue;
				cout << i << " " << ((n - 1) ^ i) << "\n";
			}
		}
	}
	else if (k == 0){
		for (int i = 0; i < n / 2; i++){
			cout << i << " " << ((n - 1) ^ i) << "\n";
		}
	}
	else{
		cout << n - 1 << " " << k << "\n";
		cout << 0 << " " << ((n - 1) ^ k) << "\n";
		for (int i = 0; i < n / 2; i++){
			if ((i == 0) || (i == min(k, (n - 1) ^ k))) continue;
			cout << i << " " << ((n - 1) ^ i) << "\n";
		}
	}
}

int main(){
	ios_base::sync_with_stdio(0);
	cin.tie(0);

	int tests;
	cin >> tests;
	while (tests--){
		solve_tc();
	}
}
