#include<cstdio>
#include<vector>

int n, ans, c[200005];
std::vector<int> v[200005];

void dfs(int x, int y) {
	int cc = 1;
	for (int i = 0; i < v[x].size(); i++) if (v[x][i] != y) {
			while (cc == c[x] || cc == c[y]) cc++;
			c[v[x][i]] = cc++;
	}
	for (int i = 0; i < v[x].size(); i++) if (v[x][i] != y) dfs(v[x][i], x);
}

int max(int x, int y) {
	return x > y ? x : y;
}

int main() {
	scanf("%d", &n);
	for (int i = 1; i < n; i++) {
		int x, y;
		scanf("%d %d", &x, &y);
		v[x].push_back(y);
		v[y].push_back(x);
	}
	c[1] = 1;
	dfs(1, 0);
	for (int i = 1; i <= n; i++) ans = max(ans, c[i]);
	printf("%d\n", ans);
	for (int i = 1; i <= n; i++) printf("%d%c", c[i], i == n ? '\n' : ' ');
}
