#include <iostream>
#include <cstdio>
#include <string>
#include <vector>
#include <algorithm>
#include <set>
#include <map>
#include <ctime>
#include <iomanip>
#include <cstdlib>
using namespace std;
struct mishen
{
    int x, r, num, res;
};


int ABS(int x)
{
    if(x < 0) x = -x;
    return x;
}


bool by_num(mishen x, mishen y)
{
    return(x.num < y.num);
}


bool by_x(mishen x, mishen y)
{
    return(x.x < y.x);
}


mishen a[10000];
int n;
int m, tx, ty, sum;


int binary(int x)
{
    int l = 0, r = n - 1, m = 0;
    while(l != r)
    {
        m = (l + r + 1) / 2;
        if(a[m].x > x)  r = m - 1;
        else            l = m;
    }
    return l;
}


int main()
{
    cin >> n;
    for(int i = 0; i < n; i++)
    {
        cin >> a[i].x >> a[i].r;
        a[i].num = i;
        a[i].res = -1;
    }
    sort(a, a + n, by_x);
    cin >> m;
    for(int i = 1; i <= m; i++)
    {
        cin >> tx >> ty;
        int temp = binary(tx);
        if(a[temp].res == -1  &&  (a[temp].x - tx) * (a[temp].x - tx) + ty * ty <= a[temp].r * a[temp].r)
        {
            sum++;
            a[temp].res = i;
        }
        if(temp  &&  a[temp - 1].res == -1  &&  (a[temp - 1].x - tx) * (a[temp - 1].x - tx) + ty * ty <= a[temp - 1].r * a[temp - 1].r)
        {
            sum++;
            a[temp - 1].res = i;
        }
        if(temp + 1 < n &&  a[temp + 1].res == -1  &&  (a[temp + 1].x - tx) * (a[temp + 1].x - tx) + ty * ty <= a[temp + 1].r * a[temp + 1].r)
        {
            sum++;
            a[temp + 1].res = i;
        }
    }
    sort(a, a + n, by_num);
    cout << sum << endl;
    for(int i = 0; i < n; i++)  cout << a[i].res << ' ';
}
